  `comment_date` datetime DEFAULT NULL ON UPDATE CURRENT_TIMESTAMP COMMENT '评论日期',
  `children` varchar(255) DEFAULT '0' COMMENT '子id',
  `upvote` int(100) DEFAULT '0' COMMENT '评论点赞数',
